mapper.select:
		SELECT s.*,aca.aca_name as  aca_name,c.`B_NAME` as b_name
  		FROM `ems_student` s
		LEFT JOIN 
		ems_academic aca ON s.aca_idx=aca.aca_idx
		LEFT JOIN `ems_class` c ON s.`B_IDX`=c.`B_IDX`
		
			1=1
			
				and s.aca_idx=#{aca_idx} 
			
			
				and s_grade=#{s_grade} 
			
			
				and s.b_idx=#{b_idx}
